import React, { useEffect, useRef } from 'react';
import { HomeCard } from '../Components/HomeCard';
import './HomePage.css';

export const HomePage = ({ movies }) => {
  const scrollRef = useRef(null);
  const intervalRef = useRef(null);

  // Create a new array with the first movie duplicated at the end
  const extendedMovies = [...movies, movies[0]];

  useEffect(() => {
    if (movies.length <= 1) return;

    let index = 0;

    intervalRef.current = setInterval(() => {
      const container = scrollRef.current;
      index++;

      container.scrollTo({
        left: index * window.innerWidth,
        behavior: 'smooth',
      });

      // If we're at the cloned slide (last one), reset to real first after animation
      if (index === extendedMovies.length - 1) {
        setTimeout(() => {
          container.scrollTo({
            left: 0,
            behavior: 'auto', // no animation, seamless jump
          });
          index = 0;
        }, 500); // wait a bit for smooth scroll to finish
      }
    }, 4000); // scroll every 4 seconds

    return () => clearInterval(intervalRef.current);
  }, [movies]);

  return (
    <>
      {movies.length === 0 ? "No Movies yet" : ""}
      <div className="fullscreen-scroll" ref={scrollRef}>
        {extendedMovies.map((movie, i) => (
          <HomeCard movie={movie} key={i} />
        ))}
      </div>
    </>
  );
};
