import React, { useEffect, useRef } from 'react';
import { HomeCard } from '../Components/HomeCard';
import './HomePage.css';

export const HomePage = (props) => {
  const scrollRef = useRef(null);

  useEffect(() => {
    const container = scrollRef.current;
    let scrollIndex = 0;

    const scrollNext = () => {
      if (!container) return;

      const totalMovies = props.movies.length;
      const viewportWidth = container.offsetWidth;

      // If we've reached the cloned first slide, reset without animation
      if (scrollIndex === totalMovies) {
        scrollIndex = 1;
        container.scrollTo({ left: 0, behavior: 'auto' });
      } else {
        scrollIndex++;
      }

      container.scrollTo({ left: viewportWidth * scrollIndex, behavior: 'smooth' });
    };

    const interval = setInterval(scrollNext, 4000); // auto-scroll every 4s
    return () => clearInterval(interval);
  }, [props.movies]);

  return (
    <>
      {props.movies.length === 0 ? "No Movies yet" : ""}
      <div className="fullscreen-scroll" ref={scrollRef}>
        {props.movies.map((movie) => (
          <HomeCard movie={movie} key={movie.sno} />
        ))}
        {/* Clone of the first card at the end to enable smooth loop */}
        {props.movies.length > 0 && <HomeCard movie={props.movies[0]} key="clone" />}
      </div>
    </>
  );
};
