import React from 'react';
import './HomeCard.css';

export const HomeCard = ({ movie }) => {
  const backgroundImageStyle = {
    backgroundImage: `url(${movie.backgroundurl})`,
  };

  return (
    <div className="movie_card">
      <div className="info_section">
        <div className="movie_header">
          <img className="locandina" src={movie.imgurl} alt="Movie poster" />
          <h1>{movie.name}</h1>
          <h4>{movie.year}</h4>
          <span className="minutes">{movie.time}</span>
          <p className="type">{movie.movieType}</p>
        </div>
        <div className="movie_desc">
          <p className="text">{movie.movieDesc}</p>
        </div>
        <div className="movie_social">
          <ul className="scrollable-icons">
            <li><i className="material-icons">share</i></li>
            <li><i className="material-icons">favorite</i></li>
            <li><i className="material-icons">chat_bubble</i></li>
            <li><i className="material-icons">thumb_up</i></li>
            <li><i className="material-icons">bookmark</i></li>
            {/* Add more if needed */}
          </ul>
        </div>
      </div>
      <div className="blur_back" style={backgroundImageStyle}></div>
    </div>
  );
};
